#!/usr/bin/env node

// Packages
const args = require('args');
const chalk = require('chalk');
const semVer = require('semver');
const inquirer = require('inquirer');
const open = require('open');
const { coroutine } = require('bluebird');
const updateNotifier = require('update-notifier');
const { red } = require('chalk');
const nodeVersion = require('node-version');

// Ours
const groupChanges = require('../lib/group');
const { branchSynced, getRepo } = require('../lib/repo');
const getCommits = require('../lib/commits');
const getChoices = require('../lib/choices');
const getTags = require('../lib/tags');
const definitions = require('../lib/definitions');
const connect = require('../lib/connect');
const createChangelog = require('../lib/changelog');
const handleSpinner = require('../lib/spinner');
const pkg = require('../package');

// Throw an error if node version is too low
if (nodeVersion.major < 6) {
  console.error(
    `${red('Error!')} Now requires at least version 6 of Node. Please upgrade!`
  );
  process.exit(1);
}

// Let user know if there's an update
// This isn't important when deployed to Now
if (pkg.dist) {
  updateNotifier({ pkg }).notify();
}

args
  .option('pre', 'Mark the release as prerelease')
  .option('overwrite', 'If the release already exists, replace it');

const flags = args.parse(process.argv);

let githubConnection;
let repoDetails;

const changeTypes = [
  {
    handle: 'major',
    name: 'Major Change',
    description: 'incompatible API change'
  },
  {
    handle: 'minor',
    name: 'Minor Change',
    description: 'backwards-compatible functionality'
  },
  {
    handle: 'patch',
    name: 'Patch',
    description: 'backwards-compatible bug fix'
  }
];

const getReleaseURL = (release, edit = false) => {
  if (!release || !release.html_url) {
    return false;
  }

  const htmlURL = release.html_url;
  return edit ? htmlURL.replace('/tag/', '/edit/') : htmlURL;
};

const createRelease = (tag, changelog, exists) => {
  const isPre = flags.pre ? 'pre' : '';
  handleSpinner.create(`Uploading ${isPre}release`);

  const methodPrefix = exists ? 'edit' : 'create';
  const method = methodPrefix + 'Release';

  const body = {
    owner: repoDetails.user,
    repo: repoDetails.repo,
    /* eslint-disable camelcase */
    tag_name: tag.tag,
    target_commitish: tag.hash,
    /* eslint-enable camelcase */
    body: changelog,
    draft: true,
    prerelease: flags.pre
  };

  if (exists) {
    body.id = exists;
  }

  githubConnection.repos[method](body, (err, response) => {
    if (err || !response.data) {
      console.log('\n');
      handleSpinner.fail('Failed to upload release.');
    }

    global.spinner.succeed();
    const releaseURL = getReleaseURL(response.data, true);

    if (releaseURL) {
      open(releaseURL);
    }

    console.log(`\n${chalk.bold('Done!')} ðŸŽ‰ Opening release in browser...`);
  });
};

const orderCommits = (commits, tags, exists) => {
  const questions = [];
  const predefined = {};

  const choices = getChoices(changeTypes, tags);

  // Show the latest changes first
  commits.reverse();

  for (const commit of commits) {
    const defTitle = definitions.type(commit.title, changeTypes);
    const defDescription = definitions.type(commit.description, changeTypes);

    const definition = defTitle || defDescription;

    if (definition) {
      predefined[commit.hash] = definition;
      continue;
    }

    questions.push({
      name: commit.hash,
      message: commit.title,
      type: 'list',
      choices
    });
  }

  global.spinner.succeed();

  // Prevents the spinner from getting succeeded
  // again once new spinner gets created
  global.spinner = false;

  console.log(
    `${chalk.green('!')} Please enter the type of change for each commit:\n`
  );

  inquirer.prompt(questions).then(
    coroutine(function*(types) {
      // Update the spinner status
      console.log('');
      handleSpinner.create('Generating the changelog');

      const results = Object.assign({}, predefined, types);
      const grouped = groupChanges(results, changeTypes);
      const changelog = yield createChangelog(grouped, commits, changeTypes);

      // Upload changelog to GitHub Releases
      createRelease(tags[0], changelog, exists);
    })
  );
};

const collectChanges = (tags, exists = false) => {
  handleSpinner.create('Loading commit history');

  getCommits(tags).then(commits => {
    for (const commit of commits) {
      if (semVer.valid(commit.title)) {
        const index = commits.indexOf(commit);
        commits.splice(index, 1);
      }
    }

    if (commits.length < 1) {
      handleSpinner.fail('No changes happened since the last release.');
    }

    orderCommits(commits, tags, exists);
  });
};

const checkReleaseStatus = coroutine(function*() {
  let tags;

  try {
    tags = yield getTags();
  } catch (err) {
    handleSpinner.fail('Directory is not a Git repository.');
  }

  if (tags.length < 1) {
    handleSpinner.fail('No tags available for release.');
  }

  const synced = yield branchSynced();

  if (!synced) {
    handleSpinner.fail('Your branch needs to be up-to-date with origin.');
  }

  githubConnection = yield connect();
  repoDetails = yield getRepo();

  handleSpinner.create('Checking if release already exists');

  githubConnection.repos.getReleases(
    {
      owner: repoDetails.user,
      repo: repoDetails.repo
    },
    (err, response) => {
      if (err) {
        handleSpinner.fail("Couldn't check if release exists.");
      }

      if (!response.data || response.data.length < 1) {
        collectChanges(tags);
        return;
      }

      let existingRelease = null;

      for (const release of response.data) {
        if (release.tag_name === tags[0].tag) {
          existingRelease = release;
          break;
        }
      }

      if (!existingRelease) {
        collectChanges(tags);
        return;
      }

      if (flags.overwrite) {
        global.spinner.text = 'Overwriting release, because it already exists';
        collectChanges(tags, existingRelease.id);

        return;
      }

      global.spinner.succeed();
      console.log('');

      const releaseURL = getReleaseURL(existingRelease);

      if (releaseURL) {
        open(releaseURL);
      }

      const alreadyThere = 'Release already exists. Opening in browser...';
      console.error(`${chalk.red('Error!')} ` + alreadyThere);

      process.exit(1);
    }
  );
});

checkReleaseStatus();

module.exports = (changes, changeTypes) => {
  const types = {};

  for (const type of changeTypes) {
    types[type.handle] = [];
  }

  for (const change in changes) {
    if (!{}.hasOwnProperty.call(changes, change)) {
      continue;
    }

    const changeType = changes[change];

    if (changeType === 'ignore') {
      continue;
    }

    types[changeType].push(change);
  }

  return types;
};

// Packages
const git = require('git-state');
const repoName = require('git-repo-name');
const repoUser = require('git-username');

// Ours
const handleSpinner = require('./spinner');

exports.getRepo = () =>
  new Promise(resolve => {
    repoName((err, repo) => {
      if (err) {
        handleSpinner.fail('Could not determine GitHub repository.');
        return;
      }

      const details = { repo };
      details.user = repoUser();

      resolve(details);
    });
  });

exports.branchSynced = () =>
  new Promise(resolve => {
    const path = process.cwd();

    const ignore = ['branch', 'stashes', 'untracked'];

    git.isGit(path, exists => {
      if (!exists) {
        return;
      }

      git.check(path, (err, results) => {
        if (err) {
          resolve(false);
          return;
        }

        for (const state of ignore) {
          delete results[state];
        }

        for (const result in results) {
          if (results[result] > 0) {
            resolve(false);
            break;
          }
        }

        resolve(true);
      });
    });
  });

// Packages
const ora = require('ora');
const { red } = require('chalk');

exports.create = message => {
  if (global.spinner) {
    global.spinner.succeed();
  }

  global.spinner = ora(message).start();
};

exports.fail = message => {
  if (global.spinner) {
    global.spinner.fail();
    console.log('');
  }

  console.error(`${red('Error!')} ${message}`);

  // eslint-disable-next-line unicorn/no-process-exit
  process.exit(1);
};

// Native
const path = require('path');

// Packages
const gitCommits = require('git-commits');

// Ours
const handleSpinner = require('./spinner');

module.exports = tags =>
  new Promise((resolve, reject) => {
    const [release, parent] = tags;

    if (!release || !parent || !parent.hash || !release.hash) {
      reject(new Error('the first release should be created manually.'));
    }

    const rev = `${parent.hash}..${release.hash}`;
    const repoPath = path.join(process.cwd(), '.git');
    const commits = [];

    gitCommits(repoPath, { rev })
      .on('data', commit => {
        // Detect and ignore the release commit
        // associated to the tag
        if (commit.hash === release.hash) {
          return;
        }

        commits.push(commit);
      })
      .on('error', () => {
        handleSpinner.fail('Not able to collect commits.');
      })
      .on('end', () => {
        resolve(commits);
      });
  });

// Packages
const inquirer = require('inquirer');
const semVer = require('semver');

module.exports = (changeTypes, tags) => {
  const list = [];
  let notNeeded;

  const releaseType = semVer.diff(tags[1].version, tags[0].version);

  switch (releaseType) {
    case 'minor':
      notNeeded = 1;
      break;
    case 'patch':
      notNeeded = 2;
      break;
    default:
      notNeeded = 0;
  }

  if (notNeeded) {
    changeTypes.splice(0, notNeeded);
  }

  for (const type of changeTypes) {
    const short = type.handle;

    list.push({
      name: `${type.name} (${type.description})`,
      value: short,
      short: '(' + short + ')'
    });
  }

  return list.concat([
    new inquirer.Separator(),
    {
      name: 'Ignore',
      short: '(ignored)',
      value: 'ignore'
    }
  ]);
};

// Packages
const semVer = require('semver');
const taggedVersions = require('tagged-versions');
const { coroutine } = require('bluebird');

module.exports = coroutine(function*(rev = 'HEAD') {
  const [tags, latest] = yield Promise.all([
    taggedVersions.getList(),
    taggedVersions.getLastVersion({ rev })
  ]);

  if (!latest) {
    return [];
  }

  for (const commit of tags) {
    if (semVer.lt(commit.version, latest.version)) {
      return [latest, commit];
    }
  }

  return [latest];
});

exports.type = (text, changeTypes) => {
  for (const type of changeTypes) {
    const handle = '(' + type.handle + ')';

    if (text.includes(handle)) {
      return type.handle;
    }
  }

  return false;
};

exports.reference = title => {
  const match = / \(#[-0-9]+\)/.exec(title);

  if (!match) {
    return false;
  }

  return match[0].replace(' (', '').replace(')', '');
};

// Native
const queryString = require('querystring');

// Packages
const request = require('request-promise-native');
const open = require('open');
const randomString = require('random-string');
const retry = require('async-retry');
const Storage = require('configstore');
const GitHubAPI = require('github');
const sleep = require('then-sleep');
const { coroutine } = require('bluebird');

// Ours
const pkg = require('../package');
const handleSpinner = require('./spinner');

// Initialize token storage
const config = new Storage(pkg.name);

const github = new GitHubAPI({
  protocol: 'https',
  headers: {
    'user-agent': `Release v${pkg.version}`
  }
});

const tokenAPI = state =>
  retry(
    () =>
      new Promise((resolve, reject) => {
        request({
          uri: 'https://release-auth.now.sh',
          qs: {
            state
          },
          json: true
        })
          .then(res => {
            if (res.status === 403) {
              reject(new Error('Unauthorized'));
            }

            if (res.error) {
              reject(res.error);
            }

            resolve(res.token);
          })
          .catch(reject);
      }),
    {
      retries: 500
    }
  );

const validateToken = token =>
  new Promise(resolve => {
    github.authenticate({
      type: 'token',
      token
    });

    // See if the token works by getting
    // the data for our company's account
    github.users.getForUser(
      {
        username: 'zeit'
      },
      err => {
        if (err) {
          resolve(false);
          return;
        }

        resolve(true);
      }
    );
  });

const loadToken = coroutine(function*() {
  if (config.has('token')) {
    const fromStore = config.get('token');
    const valid = yield validateToken(fromStore);

    return valid ? fromStore : false;
  }

  return false;
});

const requestToken = coroutine(function*() {
  let authURL = 'https://github.com/login/oauth/authorize';

  const state = randomString({
    length: 20
  });

  const params = {
    // eslint-disable-next-line camelcase
    client_id: '08bd4d4e3725ce1c0465',
    scope: 'repo',
    state
  };

  authURL += '?' + queryString.stringify(params);
  open(authURL);

  const token = yield tokenAPI(state);
  config.set('token', token);

  return token;
});

module.exports = coroutine(function*() {
  let token = yield loadToken();

  if (!token) {
    handleSpinner.create('Opening GitHub authentication page');
    yield sleep(100);

    try {
      token = yield requestToken();
    } catch (err) {
      handleSpinner.fail("Couldn't load token.");
    }
  }

  github.authenticate({
    type: 'token',
    token
  });

  return github;
});

// Packages
const { plural } = require('pluralize');
const stripWhitespace = require('trim');
const { coroutine } = require('bluebird');

// Ours
const pickCommit = require('./pick-commit');

module.exports = coroutine(function*(types, commits, changeTypes) {
  let text = '';
  const credits = new Set();

  for (const type in types) {
    if (!{}.hasOwnProperty.call(types, type)) {
      continue;
    }

    const changes = types[type];

    if (changes.length < 1) {
      continue;
    }

    const typeInfo = changeTypes.filter(item => {
      return item.handle === type;
    })[0];

    // Add heading
    text += `### ${plural(typeInfo.name)} \n\n`;

    // Find last change, in order to be able
    // to add a newline after it
    const lastChange = changes[changes.length - 1];

    for (const change of changes) {
      const changeDetails = yield pickCommit(change, commits, changeTypes);

      if (changeDetails.text) {
        text += changeDetails.text;
      }

      if (changeDetails.credits && changeDetails.credits.length > 0) {
        changeDetails.credits.forEach(item => {
          // Don't add bots to the credits
          if (item.includes('[bot]')) {
            return;
          }

          credits.add(item);
        });
      }

      if (change === lastChange) {
        text += '\n';
      }
    }
  }

  if (credits.size > 0) {
    text += '### Credits \n\n';
    text += 'Huge thanks to ';

    // GitHub links usernames if prefixed with @
    let index = 1;
    credits.forEach(credit => {
      text += `@${credit}`;

      const penultimate = index === credits.size - 1;
      const notLast = index !== credits.size;

      if (penultimate) {
        text += ' and ';
      } else if (notLast) {
        text += ', ';
      }

      index += 1;
    });

    text += ' for their help!';
    text += '\n';
  }

  // Remove newlines from the end
  return stripWhitespace.right(text);
});

// Packages
const { coroutine } = require('bluebird');

// Ours
const cleanCommitTitle = require('./clean-title');
const getCredits = require('./credits');

module.exports = coroutine(function*(hash, all, changeTypes) {
  const related = all.filter(item => {
    return item.hash === hash;
  })[0];

  const title = cleanCommitTitle(related.title, changeTypes);
  let credits = [];

  if (title.ref) {
    hash = title.ref;

    const rawHash = hash.split('#')[1];

    // Retrieve users that have collaborated on a change
    const collaborators = yield getCredits(rawHash);

    if (collaborators) {
      credits = credits.concat(collaborators);
    }
  }

  return {
    text: `- ${title.content}: ${hash}\n`,
    credits
  };
});

// Packages
const capitalize = require('capitalize');
const stripWhitespace = require('trim');

// Ours
const definitions = require('./definitions');

module.exports = (title, changeTypes) => {
  const toReplace = {
    type: definitions.type(title, changeTypes),
    ref: definitions.reference(title)
  };

  for (const definition in toReplace) {
    if (!{}.hasOwnProperty.call(toReplace, definition)) {
      continue;
    }

    const state = toReplace[definition];

    if (state) {
      title = title.replace('(' + state + ')', '');
    }
  }

  return {
    content: stripWhitespace(capitalize(title)),
    ref: toReplace.ref
  };
};

// Packages
const { coroutine } = require('bluebird');

// Ours
const connect = require('./connect');
const repo = require('./repo');

const getPullRequest = number =>
  new Promise(
    coroutine(function*(resolve, reject) {
      const repoDetails = yield repo.getRepo();
      const github = yield connect();

      github.pullRequests.get(
        {
          owner: repoDetails.user,
          repo: repoDetails.repo,
          number
        },
        (err, results) => {
          if (err || !results.data) {
            reject(err);
            return;
          }

          resolve(results.data);
        }
      );
    })
  );

module.exports = coroutine(function*(number) {
  let data;

  try {
    data = yield getPullRequest(number);
  } catch (err) {
    return;
  }

  if (data.user) {
    return [data.user.login];
  }

  return false;
});
